// ===============================
// FUNGSI MENAMPILKAN PRODUK
// ===============================
function tampilkanProduk(dataProduk) {
    const container = document.getElementById("daftarProduk");
    container.innerHTML = "";

    dataProduk.forEach(item => {
        container.innerHTML += `
            <div class="produk-card">
                <img src="${item.gambar}">
                <h3>${item.nama}</h3>
                <p class="kategori">${item.kategori}</p>
                <p>${item.deskripsi}</p>
                <p class="harga">Rp ${item.harga.toLocaleString()}</p>
            </div>
        `;
    });
}

// ===============================
// FUNGSI FILTER KATEGORI
// ===============================
function filterProduk() {
    const kategoriDipilih = document.getElementById("filterKategori").value;

    if (kategoriDipilih === "all") {
        tampilkanProduk(produk);
    } else {
        const hasilFilter = produk.filter(
            item => item.kategori === kategoriDipilih
        );
        tampilkanProduk(hasilFilter);
    }
}

// ===============================
// TAMPILKAN SEMUA PRODUK SAAT AWAL
// ===============================
tampilkanProduk(produk);
